import { useEffect, useState } from "react";

const Editor = () => {
  const [fontNameOptions, setFontNameOptions] = useState([]);
  const [fontSizeOptions, setFontSizeOptions] = useState([]);
  let optionsButtons = document.querySelectorAll(".option-button");
  let advancedOptionButton = document.querySelectorAll(".adv-option-button");
  let fontName = document.getElementById("fontName");
  let fontSizeRef = document.getElementById("fontSize");
  let writingArea = document.getElementById("text-input");
  let linkButton = document.getElementById("createLink");
  let alignButtons = document.querySelectorAll(".align");
  let spacingButtons = document.querySelectorAll(".spacing");
  let formatButtons = document.querySelectorAll(".format");
  let scriptButtons = document.querySelectorAll(".script");

  //List of fontlist

  //   let fontSizeRef = document.getElementById("fontSize");

  useEffect(() => {
    const initializer = () => {
      // function calls for highlighting buttons
      // No highlights for link, unlink,lists, undo,redo since they are one time operations
      highlighter(alignButtons, true);
      highlighter(spacingButtons, true);
      highlighter(formatButtons, false);
      highlighter(scriptButtons, true);
      // create options for font names
    };
    const fontList = [
      "Arial",
      "Verdana",
      "Times New Roman",
      "Garamond",
      "Georgia",
      "Courier New",
      "cursive",
    ];
    setFontNameOptions(
      fontList.map((value, index) => (
        <option key={index} value={value}>
          {value}
        </option>
      ))
    );

    const sizeOptions = [];
    for (let i = 1; i <= 7; i++) {
      sizeOptions.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
    }

    setFontSizeOptions(sizeOptions);
    // fontSizeRef.value = 3;
  }, []);

  //Initial Settings

  //main logic
  const handleButtonClick = (command) => {
    //execCommand executes command on selected text
    console.log("Executing command:", command);
    document.execCommand(command, false, null);
  };
  const handleAdvancedOptionChange = (command, value) => {
    document.execCommand(command, false, value);
  };
  //   //For basic operations which don't need value parameter
  //   optionsButtons.forEach((button) => {
  //     button.addEventListener("click", () => {
  //       modifyText(button.id, false, null);
  //     });
  //   });
  //options that require value parameter (e.g colors, fonts)
  //   advancedOptionButton.forEach((button) => {
  //     button.addEventListener("change", () => {
  //       modifyText(button.id, false, button.value);
  //     });
  //   });
  //link
  //   linkButton.addEventListener("click", () => {
  //     let userLink = prompt("Enter a URL");
  //     //if link has http then pass directly else add https
  //     if (/http/i.test(userLink)) {
  //       modifyText(linkButton.id, false, userLink);
  //     } else {
  //       userLink = "http://" + userLink;
  //       modifyText(linkButton.id, false, userLink);
  //     }
  //     window.open(userLink, "_blank");
  //   });
  const handleLinkButtonClick = () => {
    let userLink = prompt("Enter a URL");
    if (userLink) {
      // Validate the URL using regular expression
      const urlPattern = /^(https?:\/\/)?([\w-]+\.)+[\w-]+(\/[\w- .\/?%&=]*)?$/;
      if (urlPattern.test(userLink)) {
        // URL is valid, create the link
        if (
          !userLink.startsWith("http://") &&
          !userLink.startsWith("https://")
        ) {
          userLink = "http://" + userLink;
        }
        document.execCommand("createLink", false, userLink);
      } else {
        alert("Invalid URL");
      }
    }
  };
  //Highlight clicked button
  const highlighter = (className, needsRemoval) => {
    className.forEach((button) => {
      button.addEventListener("click", () => {
        //needsRemoval = true means only one button should be highlight and other would be normal
        if (needsRemoval) {
          let alreadyActive = false;
          //If currently clicked button is already active
          if (button.classList.contains("active")) {
            alreadyActive = true;
          }
          //Remove highlight from other buttons
          highlighterRemover(className);
          if (!alreadyActive) {
            //highlight clicked button
            button.classList.add("active");
          }
        } else {
          //if other buttons can be highlighted
          button.classList.toggle("active");
        }
      });
    });
  };
  const highlighterRemover = (className) => {
    className.forEach((button) => {
      button.classList.remove("active");
    });
  };
  window.onload = initializer();

  return (
    <>
      <div>
        <div className="container">
          <div className="options">
            <button
              id="bold"
              className="option-button format"
              onClick={() => handleButtonClick("bold")}
            >
              <i className="fa-solid fa-bold"></i>
            </button>
            <button
              id="italic"
              className="option-button format"
              onClick={() => handleButtonClick("italic")}
            >
              <i className="fa-solid fa-italic"></i>
            </button>
            <button
              id="underline"
              className="option-button format"
              onClick={() => handleButtonClick("underline")}
            >
              <i className="fa-solid fa-underline"></i>
            </button>
            <button
              id="strikeout"
              className="option-button format"
              onClick={() => handleButtonClick("strikeout")}
            >
              <i className="fa-solid fa-strikethrough"></i>
            </button>
            <button
              id="superscript"
              className="option-button format"
              onClick={() => handleButtonClick("superscript")}
            >
              <i className="fa-solid fa-superscript"></i>
            </button>
            <button
              id="subscript"
              className="option-button format"
              onClick={() => handleButtonClick("subscript")}
            >
              <i className="fa-solid fa-subscript"></i>
            </button>
            <button
              id="insertOrderedList"
              className="option-button"
              onClick={() => handleButtonClick("insertOrderedList")}
            >
              <i className="fa-solid fa-list-ol"></i>
            </button>
            <button
              id="insertUnorderedList"
              className="option-button"
              onClick={() => handleButtonClick("insertUnorderedList")}
            >
              <i className="fa-solid fa-list"></i>
            </button>
            <button
              id="undo"
              className="option-button"
              onClick={() => handleButtonClick("undo")}
            >
              <i className="fa-solid fa-rotate-left"></i>
            </button>
            <button
              id="redo"
              className="option-button"
              onClick={() => handleButtonClick("redo")}
            >
              <i className="fa-solid fa-rotate-right"></i>
            </button>
            <button
              id="createLink"
              className="option-button"
              onClick={() => handleButtonClick("createLink")}
            >
              <i className="fa fa-link"></i>
            </button>
            <button
              id="unlink"
              className="option-button"
              onClick={() => handleButtonClick("unlink")}
            >
              <i className="fa fa-unlink"></i>
            </button>

            <button
              id="justifyLeft"
              className="option-button align"
              onClick={() => handleButtonClick("justifyLeft")}
            >
              <i className="fa-solid fa-align-left"></i>
            </button>

            <button
              id="justifyCenter"
              className="option-button align"
              onClick={() => handleButtonClick("justifyCenter")}
            >
              <i className="fa-solid fa-align-center"></i>
            </button>
            <button
              id="justifyRight"
              className="option-button align"
              onClick={() => handleButtonClick("justifyRight")}
            >
              <i className="fa-solid fa-align-right"></i>
            </button>
            <button
              id="justifyFull"
              className="option-button align"
              onClick={() => handleButtonClick("justifyFull")}
            >
              <i className="fa-solid fa-align-justify"></i>
            </button>
            <button
              id="indent"
              className="option-button spacing"
              onClick={() => handleButtonClick("indent")}
            >
              <i className="fa-solid fa-indent"></i>
            </button>
            <button
              id="outdent"
              className="option-button spacing"
              onClick={() => handleButtonClick("outdent")}
            >
              <i className="fa-solid fa-outdent"></i>
            </button>

            <select
              id="formatBlock"
              className="adv-option-button"
              onChange={(e) =>
                handleAdvancedOptionChange("formatBlock", e.target.value)
              }
            >
              <option value="H1">H1</option>
              <option value="H2">H2</option>
              <option value="H3">H3</option>
              <option value="H4">H4</option>
              <option value="H5">H5</option>
              <option value="H6">H6</option>
            </select>

            <select
              id="fontName"
              className="adv-option-button"
              onChange={(e) =>
                handleAdvancedOptionChange("fontName", e.target.value)
              }
            >
              {fontNameOptions}
            </select>
            <select
              id="fontSize"
              className="adv-option-button"
              defaultValue="3"
              onChange={(e) =>
                handleAdvancedOptionChange("fontSize", e.target.value)
              }
            >
              {fontSizeOptions}
            </select>

            <div className="input-wrapper">
              <input
                type="color"
                id="foreColor"
                className="adv-option-button"
                onChange={(e) =>
                  handleAdvancedOptionChange("foreColor", e.target.value)
                }
              />
              <label htmlFor="foreColor">Font Color</label>
            </div>

            <div className="input-wrapper">
              <input
                type="color"
                id="backColor"
                className="adv-option-button"
                onChange={(e) =>
                  handleAdvancedOptionChange("backColor", e.target.value)
                }
              />
              <label htmlFor="backColor">Highlight Color</label>
            </div>
          </div>
          <div id="text-input" contentEditable="true"></div>
        </div>
      </div>
    </>
  );
};

export default Editor;
