import { useEffect, useState } from "react";
import useFetch from "../useFetch";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { removeNote, addNotes } from "../features/noteSlice";
const EditorNew = () => {
  const [data, setData] = useState("");

  const [Loading, setIsLoading] = useState(false);
  const [content, setContent] = useState("");
  const dispatch = useDispatch();
  const { id } = useParams();
  useEffect(() => {
    const fetchNote = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/notes/${id}/`);
        if (!response.ok) {
          throw new Error("Failed to fetch note");
        }
        const data = await response.json();
        setData(data);
        setContent(data.content);
        dispatch(addNotes(data.title));

        setIsLoading(false);
      } catch (error) {
        console.error("Error fetching note:", error);
        setIsLoading(false);
      }
    };

    fetchNote();
  }, [id]);
  const title = useSelector((state) => state.newdata);

  return (
    <>
      <div>
        <div className="navbar">
          <div className="hamburger" onClick={toggleSidebar}>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
            <div className={`bar ${isOpen ? "open" : ""}`}></div>
          </div>
          <div className="title-container">
            <div>
              <img
                src="./favicon.png"
                alt=""
                srcset=""
                height="52px"
                onClick={changeDone}
              />
            </div>
            <div>
              {pathname == "/editor" && (
                <div onClick={changeName} className="titles">
                  {content ? (
                    <div>
                      <input
                        className="text-box"
                        type="text"
                        value={title}
                        onChange={handleTitleChange}
                        autoFocus
                      />
                    </div>
                  ) : (
                    title
                  )}
                </div>
              )}
              {pathname == "/" && !content && (
                <div className="titles">Notepad</div> // Move this outside the inner div
              )}
              {pathname !== `/editor` && pathname !== "/" && !content && (
                <div className="titles">{title}</div>
              )}
            </div>
          </div>
          <div className={`sidebar ${isOpen ? "open" : ""}`}>
            <ul>
              <li>Home</li>
              <li>About</li>
              <li>Contact</li>
            </ul>
          </div>
        </div>
        <div className="show" dangerouslySetInnerHTML={{ __html: content }}>
          {/* {content && content} */}
        </div>
      </div>
    </>
  );
};

export default EditorNew;
