import React, { useState } from "react";
import "../style.css";

function Navbar() {
  const [content, setContent] = useState(false);
  const [title, setTitle] = useState("Untitled");
  const Sidebar = () => {
    const [isOpen, setIsOpen] = useState(false);

    const toggleSidebar = () => {
      setIsOpen(!isOpen);
    };
    const handleButtonClick = (command, needsRemoval) => {
      //execCommand executes command on selected text
      console.log("Executing command:", command);
      document.execCommand(command, false, null);
      if (needsRemoval) {
        const alreadyActive = activeButtons.includes(command);
        setActiveButtons(alreadyActive ? [] : [command]);
      } else {
        setActiveButtons((prevButtons) =>
          prevButtons.includes(command)
            ? prevButtons.filter((button) => button !== command)
            : [...prevButtons, command]
        );
      }
    };

    return (
      <div className="title-container">
        <div>
          <img
            src="favicon.png"
            alt=""
            srcset=""
            height="52px"
            onClick={() => setContent(false)}
          />
        </div>
        <div>
          {content ? (
            <div>
              <input
                className="text-box"
                type="text"
                value={title}
                onChange={handleTitleChange}
              />
            </div>
          ) : (
            <div onClick={changeName} className="titles">
              {" "}
              {title}
            </div>
          )}
        </div>
      </div>
      //   <div className={`sidebar ${isOpen ? "open" : ""}`}>
      //     <div className="sidebar-toggle" onClick={toggleSidebar}>
      //       <div className="bar"></div>
      //       <div className="bar"></div>
      //       <div className="bar"></div>
      //     </div>
      //     <ul>
      //       <li>Home</li>
      //       <li>About</li>
      //       <li>Contact</li>
      //     </ul>
      //   </div>
    );
  };

  return <Sidebar />;
}

export default Navbar;
