import React, { useEffect } from "react";
import useFetch from "../useFetch";

export default function NoteList() {
  const apiUrl = process.env.REACT_APP_API_URL;
  //   const { data, isPending, error } = useFetch(`${apiUrl}/notes/`);
  const { data, isPending, error } = useFetch(`http://127.0.0.1:8000/notes/`);

  useEffect(() => {
    // const handleDarkLightToggle = () => {
    //   document.body.classList.add("toggle");
    //   setTimeout(() => {
    //     document.body.classList.toggle("light");
    //     setTimeout(() => document.body.classList.remove("toggle"), 10);
    //   }, 5);
    // };

    const handleDarkLightToggle = () => {
      document.body.classList.add("toggle");
      setTimeout(() => {
        document.body.classList.toggle("light");
        setTimeout(() => document.body.classList.remove("toggle"), 10);
      }, 5);
    };

    const inputElement = document.querySelector(".day-night input");
    if (inputElement) {
      inputElement.addEventListener("change", handleDarkLightToggle);
    }

    return () => {
      if (inputElement) {
        inputElement.removeEventListener("change", handleDarkLightToggle);
      }
    };
  }, []); // Empty dependency array ensures this effect runs only once

  useEffect(() => {
    console.log("This is api url ", data);
  }, [data, isPending, error]);

  return (
    <div>
      <div className="grid">
        {/* Check if data is available before mapping */}
        {data &&
          data.map((note) => (
            <div className="card" key={note.id}>
              <h4>{note.title}</h4>
              <p style={{ fontSize: textSizes[index]?.fontSize }}>
                {note.content.substring(0, 200)}{" "}
                {/* Display first 200 characters */}
                {note.content.length > 200 && "..."}{" "}
                {/* Add ellipsis if content length exceeds 200 */}
              </p>
              <div className="shine"></div>
              <div className="background">
                <div className="tiles">
                  <div className="tile tile-1"></div>
                  <div className="tile tile-2"></div>
                  <div className="tile tile-3"></div>
                  <div className="tile tile-4"></div>
                  <div className="tile tile-5"></div>
                  <div className="tile tile-6"></div>
                  <div className="tile tile-7"></div>
                  <div className="tile tile-8"></div>
                  <div className="tile tile-9"></div>
                  <div className="tile tile-10"></div>
                </div>
                <div className="line line-1"></div>
                <div className="line line-2"></div>
                <div className="line line-3"></div>
              </div>
            </div>
          ))}
      </div>
    </div>
  );
}
