import React, { useEffect, useState } from "react";
import useFetch from "../useFetch";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { removeNote } from "../features/noteSlice";

export default function NoteList() {
  const [bdata, setBdata] = useState(null);
  const apiUrl = process.env.REACT_APP_API_URL;
  const notes = useSelector((state) => state.notes);
  console.log("This is from the notelist", notes);
  const dispatch = useDispatch();
  //   const data = notes;
  const { data, isPending, error } = useFetch(`http://127.0.0.1:8000/notes/`);
  useEffect(() => {
    console.log("I am the actual data ", data);
    if (data && data.length > 0) {
      const newData = data.map((item) => ({
        id: item.id,
        title: item.title,
        content: item.content,
      }));

      newData.forEach((item) => {
        console.log("this is from navbar", item);
        dispatch(addNotes(item));
      });
    }
  }, [data, isPending, error]);

  useEffect(() => {
    // const handleDarkLightToggle = () => {
    //   document.body.classList.add("toggle");
    //   setTimeout(() => {
    //     document.body.classList.toggle("light");
    //     setTimeout(() => document.body.classList.remove("toggle"), 10);
    //   }, 5);
    // };

    const handleDarkLightToggle = () => {
      document.body.classList.add("toggle");
      setTimeout(() => {
        document.body.classList.toggle("light");
        setTimeout(() => document.body.classList.remove("toggle"), 10);
      }, 5);
    };

    const inputElement = document.querySelector(".day-night input");
    if (inputElement) {
      inputElement.addEventListener("change", handleDarkLightToggle);
    }

    return () => {
      if (inputElement) {
        inputElement.removeEventListener("change", handleDarkLightToggle);
      }
    };
  }, []); // Empty dependency array ensures this effect runs only once

  useEffect(() => {
    //   data.map((note) => (
    //     <div className="card" key={note.id}>
    //       <h4>{note.title}</h4>
    //       <p>
    //         {note.content.substring(0, 200)}{" "}
    //         {note.content.length > 200 && "..."}{" "}
  }, [data]);

  return (
    <div>
      <div className="grid">
        <Link to={`/editor`} style={{ textDecoration: "none" }}>
          <div className="card">
            <i class="fa-solid fa-plus"></i>
          </div>
        </Link>

        {/* Check if data is available before mapping */}
        {data &&
          data.map((note) => (
            <Link
              key={note.id}
              to={`/editor/${note.id}`}
              style={{ textDecoration: "none" }}
            >
              <div className="card" key={note.id}>
                <h4>{note.title}</h4>
                <br />
                <div>
                  {note.content.substring(0, 100)}
                  {note.content.length > 100 && "..."}{" "}
                </div>
              </div>
            </Link>
          ))}
      </div>
    </div>
  );
}
