import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

const Editor = () => {
  const [content, setContent] = useState("");
  const [body, setBody] = useState("");
  const [isPending, setIsPending] = useState(false);
  const [activeButtons, setActiveButtons] = useState([]);
  const apiUrl = process.env.REACT_APP_API_URL;
  const navigate = useNavigate();

  useEffect(() => {
    const fontList = [
      "Arial",
      "Verdana",
      "Times New Roman",
      "Garamond",
      "Georgia",
      "Courier New",
      "cursive",
    ];
    const fontNameOptions = fontList.map((value, index) => (
      <option key={index} value={value}>
        {value}
      </option>
    ));

    const sizeOptions = [];
    for (let i = 1; i <= 7; i++) {
      sizeOptions.push(
        <option key={i} value={i}>
          {i}
        </option>
      );
    }

    setFontSizeOptions(sizeOptions);
  }, []);

  const handleSave = (e) => {
    e.preventDefault();
    const blog = { content }; // Assuming 'content' is the data you want to save
    setIsPending(true);
    fetch("http://127.0.0.1:8000/notes/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(blog),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error("Failed to save data");
        }
        console.log("Adding new notes");
        return response.json();
      })
      .then(() => {
        setIsPending(false);
        navigate("/");
      })
      .catch((error) => {
        console.error("Error saving data:", error);
        setIsPending(false);
      });
  };

  const handleButtonClick = (command, needsRemoval) => {
    console.log("Executing command:", command);
    document.execCommand(command, false, null);
    if (needsRemoval) {
      const alreadyActive = activeButtons.includes(command);
      setActiveButtons(alreadyActive ? [] : [command]);
    } else {
      setActiveButtons((prevButtons) =>
        prevButtons.includes(command)
          ? prevButtons.filter((button) => button !== command)
          : [...prevButtons, command]
      );
    }
  };

  const handlesetItem = (e) => {
    setContent(e.target.value);
  };

  const handleAdvancedOptionChange = (command, value) => {
    document.execCommand(command, false, value);
  };

  return (
    <>
      <div>
        <div className="container">
          <div className="options">
            <button
              id="save"
              className="option-button format"
              onClick={handleSave}
              onChange={handlesetItem}
            >
              Save
            </button>
            {/* Other buttons and options */}
          </div>
          <div id="text-input" contentEditable="true"></div>
        </div>
      </div>
    </>
  );
};

export default Editor;
