import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { removeNote } from "../features/noteSlice";

export default function NoteList() {
  const [loading, setLoading] = useState(true);
  const [notes, addNotes] = useState(null);
  const dispatch = useDispatch();
  //   const notes = useSelector((state) => state.notes);

  useEffect(() => {
    fetch("`http://127.0.0.1:8000/notes/`")
      .then((response) => response.json())
      .then((data) => {
        dispatch(addNotes(data)); // Assuming addNotes action adds notes to Redux store
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching data:", error);
        setLoading(false);
      });
  }, [dispatch]);

  const handleRemoveNote = (id) => {
    dispatch(removeNote(id));
  };

  return (
    <div>
      <div className="grid">
        <Link to={`/editor`} style={{ textDecoration: "none" }}>
          <div className="card addDiv">
            <i class="fa-solid fa-plus"></i>
          </div>
        </Link>

        {loading ? (
          <div>Loading...</div>
        ) : (
          // Check if data is available before mapping
          notes.map((note) => (
            <div className="card" key={note.id}>
              <h4>{note.title}</h4>
              <br />
              <div>
                {note.content.substring(0, 100)}
                {note.content.length > 100 && "..."}{" "}
              </div>
              <button onClick={() => handleRemoveNote(note.id)}>Remove</button>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
